import AuthService from "../services/auth-service";
import React, {Component} from 'react';
import CheckButton from "react-validation/build/button";

const required = value => {
    if (!value) {
      return (
        <div className="alert alert-danger" role="alert">
          This field is required!
        </div>
      );
    }
  };

class Login extends Component {
    constructor(props) {
        super(props);
        this.handleLogin = this.handleLogin.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);

        this.state = {
          username: "",
          password: "",
          loading: false,
          message: ""
        };
      }

      onChangeUsername(e) {
        this.setState({
          username: e.target.value
        });
      }

      onChangePassword(e) {
        this.setState({
          password: e.target.value
        });
      }

      handleLogin(e) {
        e.preventDefault();

        this.setState({
          message: "",
          loading: true
        });

        this.form.validateAll();

        if (this.checkBtn.context._errors.length === 0) {
          AuthService.login(this.state.username, this.state.password).then(
            () => {
              this.props.router.navigate("/");
              window.location.reload();
            },
            error => {
              const resMessage =
                (error.response &&
                  error.response.data &&
                  error.response.data.message) ||
                error.message ||
                error.toString();

              this.setState({
                loading: false,
                message: resMessage
              });
            }
          );
        } else {
          this.setState({
            loading: false
          });
        }
      }

    render () {
        return (
            <div className="flex w-full justify-center items-center  bg-gradient-to-r from-slate-500 to-blue-900">
                <div className="flex md:flew-row flex-col items-start justify-between md:p-20 py-12 px-4 ">
                    <div className="flex flex-1 justify-start flex-col md:mr-10">
                        <h1 className="text-3xl sm:text-5xl text-white text-gradient  py-4">
                            Speak Friend and Enter! üîê
                        </h1>

            <form onSubmit={this.handleLogin} ref={c => { this.form = c; }}>
                <div class="mt-10">
                    <div>
                        <label class="block" for="email"><h1 className=" text-white">Name</h1></label>
                                <input 
                                    type="text"
                                    placeholder="Name"
                                    class="w-full px-4 py-2 mt-10 border rounded-md focus:outline-none focus:ring-1 focus:ring-blue-600"
                                    name="username"
                                    value={this.state.username}
                                    onChange={this.onChangeUsername}
                                    validations={[required]}
                                />
                    </div>
                    <div class="mt-10">
                        <label class="block"><h1 className="text-white">Password</h1></label>
                                <input
                                    type="password"
                                    placeholder="Password"
                                    class="mt-10 w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-blue-600"
                                    name="password"
                                    value={this.state.password}
                                    onChange={this.onChangePassword}
                                    validations={[required]}
                                />
                    </div>
                    <div class="flex items-baseline justify-center">
                        <button class="px-6 py-2 mt-10 text-white bg-blue-600 rounded-lg hover:bg-blue-900" disabled={this.state.loading}>
                            {this.state.loading && (
                                <span className="spinner-border spinner-border-sm"></span>
                            )}
                            <span>Login</span>
                        </button>
                    </div>
                </div>
                <div className="form-group">
              <button
                className="btn btn-primary btn-block"
                disabled={this.state.loading}
              >
                {this.state.loading && (
                  <span className="spinner-border spinner-border-sm"></span>
                )}
                <span>Login</span>
              </button>
            </div>

            {this.state.message && (
              <div className="form-group">
                <div className="alert alert-danger" role="alert">
                  {this.state.message}
                </div>
              </div>
            )}
            <CheckButton
              style={{ display: "none" }}
              ref={c => {
                this.checkBtn = c;
              }}
            />
            </form>
                    </div>
                </div>
            </div>

        );
    }
};

export default Login;
